import React, { useEffect, useState } from "react";
import jsPDF from "jspdf";
import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Accordion } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import GenericModal from "../../../components/GenericModal/GenericModal.jsx";

const Measurements = () => {
  // Define las opciones para los select
  const tipoAberturaOptions = ["Ventana", "Puerta", "Otro"];
  const serieOptions = [
    "Modena 2",
    "Modena 78",
    "A30 New",
    "25 Max",
    "Monaco RPT",
    "Probba",
    "Gala",
    "Otra",
  ];
  const modoAperturaOptions = [
    "Adentro",
    "Afuera",
    "Corrediza",
    "Fijo",
    "Batiente",
    "Oscilobatiente",
    "Proyectante",
    "Tabaquera",
    "Guillotina",
  ];
  const vidrioOptions = [
    "4mm",
    "5mm",
    "DVH",
    "3+3",
    "4+4",
    "Solarcool",
    "Opacid",
  ];
  const colorOptions = ["Anodizado", "Blanco", "Anolok", "Negro", "Pintado"];
  const colocacionOptions = ["Suministro", "En seco", "Húmedo"];
  const cierresOptions = ["Manijas", "Lara", "Laterales Embutidos"];
  const guiasHojasOptions = ["2R 2H", "2R 3H", "2R 4H", "3R 3H", "3R 6H"];
  const monoblocCajonOptions = ["PVC", "3+1", "Aluminio"];
  const monoblocLamasOptions = ["PVC", "Aluminio"];
  const monoblocLamasTamOptions = ["40mm", "55mm"];
  const mosquiteroOptions = ["Simple", "Reforzado", "Fijo"];
  const conectoresTubosOptions = [
    "Lateral",
    "Superior",
    "Inferior",
    "Perimetral",
  ];

  // Recupera el estado de localStorage o inicializa
  const [measurements, setMeasurements] = useState(() => {
    const savedMeasurements = localStorage.getItem("measurements");
    return savedMeasurements
      ? JSON.parse(savedMeasurements)
      : [
          {
            nombreAbertura: "Abertura 1",
            cantidad: 1,
            tipoAbertura: "",
            series: [],
            modoApertura: "",
            length: "",
            ancho: "",
            cantidadGuiasHojas: [],
            tipoVidrio: [],
            color: [],
            monobloc: false,
            monoblocCajonOpciones: [],
            monoblocLamasOpciones: [],
            monoblocLamasTamOpciones: [],
            mosquitero: false,
            mosquiteroOpciones: [],
            cierres: [],
            tapajuntas: false,
            premarco: false,
            colocacion: [],
            cerraduras: 0,
            conectores: false,
            conectoresOpciones: [],
            tubos: false,
            tubosOpciones: [],
            detalles: [],
          },
        ];
  });

  // Recupera o inicializa los datos del cliente
  const [clientInfo, setClientInfo] = useState(() => {
    const savedClientInfo = localStorage.getItem("clientInfo");
    return savedClientInfo
      ? JSON.parse(savedClientInfo)
      : {
          nombre: "",
          celular: "",
          direccion: "",
          email: "",
        };
  });

  // Efecto para guardar measurements en localStorage
  useEffect(() => {
    localStorage.setItem("measurements", JSON.stringify(measurements));
  }, [measurements]);

  // Efecto para guardar clientInfo en localStorage
  useEffect(() => {
    localStorage.setItem("clientInfo", JSON.stringify(clientInfo));
  }, [clientInfo]);

  const handleAddMeasurement = () => {
    setMeasurements([
      ...measurements,
      {
        nombreAbertura: `Abertura ${measurements.length + 1}`,
        cantidad: 1,
        tipoAbertura: "",
        series: [],
        modoApertura: "",
        length: "",
        ancho: "",
        cantidadGuiasHojas: [],
        tipoVidrio: [],
        color: [],
        monobloc: false,
        monoblocCajonOpciones: [],
        monoblocLamasOpciones: [],
        monoblocLamasTamOpciones: [],
        mosquitero: false,
        mosquiteroOpciones: [],
        cierres: [],
        tapajuntas: false,
        premarco: false,
        colocacion: [],
        cerraduras: 0,
        conectores: false,
        conectoresOpciones: [],
        tubos: false,
        tubosOpciones: [],
        detalles: [],
      },
    ]);
  };

  const [showModal, setShowModal] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(null);
  const handleShowModal = (index) => {
    setCurrentIndex(index);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setCurrentIndex(null);
  };
  const handleConfirmDelete = () => {
    handleRemoveMeasurement(currentIndex);
    handleCloseModal();
  };

  const handleInputChange = (index, event) => {
    const { name, value, type, checked } = event.target;
    const newMeasurements = [...measurements];

    let processedValue = value;
    if ((name === "ancho" || name === "length") && typeof value === "string") {
      console.log("Converting: " + value);
      processedValue = value.replace(",", ".");
    }

    if (type === "checkbox") {
      newMeasurements[index][name] = checked;
    } else {
      newMeasurements[index][name] = processedValue;
    }

    if (name === "modoApertura") {
      newMeasurements[index][name] = value;

      if (value !== "Corrediza") {
        newMeasurements[index].cantidadGuiasHojas = [];
      }
    }

    if (name === "tipoAbertura") {
      newMeasurements[index][name] = value;

      if (value !== "Ventana") {
        newMeasurements[index].cantidadGuiasHojas = [];
      }
    }

    if (name === "conectores") {
      newMeasurements[index][name] = checked;

      if (!checked) {
        newMeasurements[index].conectoresOpciones = [];
      }
    }

    if (name === "tubos") {
      newMeasurements[index][name] = checked;

      if (!checked) {
        newMeasurements[index].tubosOpciones = [];
      }
    }

    setMeasurements(newMeasurements);
  };

  const handleCheckboxChange = (index, name, value) => {
    const newMeasurements = [...measurements];
    const currentValues = newMeasurements[index][name];
    if (currentValues.includes(value)) {
      newMeasurements[index][name] = currentValues.filter(
        (item) => item !== value
      );
    } else {
      newMeasurements[index][name] = [...currentValues, value];
    }
    setMeasurements(newMeasurements);
  };

  const handleClientInputChange = (event) => {
    const { name, value } = event.target;
    setClientInfo({ ...clientInfo, [name]: value });
  };

  const handleRemoveMeasurement = (index) => {
    const newMeasurements = [...measurements];
    newMeasurements.splice(index, 1);
    setMeasurements(newMeasurements);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    for (let i = 0; i < measurements.length; i++) {
      const measurement = measurements[i];
      if (measurement.series.length === 0) {
        alert(
          `Por favor, selecciona al menos una serie para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (measurement.color.length === 0) {
        alert(
          `Por favor, selecciona al menos un color para ${measurement.nombreAbertura}`
        );
      }
      if (
        measurement.modoApertura === "Corrediza" &&
        measurement.cantidadGuiasHojas.length === 0
      ) {
        alert(
          `Por favor, selecciona al menos una cantidad para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (
        measurement.tipoAbertura !== "Otro" &&
        measurement.tipoVidrio.length === 0
      ) {
        alert(
          `Por favor, selecciona al menos un tipo de vidrio para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (
        measurement.mosquitero &&
        measurement.mosquiteroOpciones.length === 0
      ) {
        alert(
          `Por favor, selecciona al menos una opción de MOSQUITERO para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (
        measurement.monobloc &&
        (measurement.monoblocCajonOpciones.length === 0 ||
          measurement.monoblocLamasOpciones.length === 0 ||
          measurement.monoblocLamasTamOpciones.length === 0)
      ) {
        alert(
          `Por favor, selecciona al menos una opción en cada item de MONOBLOC para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (
        measurement.conectores &&
        measurement.conectoresOpciones.length === 0
      ) {
        alert(
          `Por favor, selecciona al menos una opción de CONECTORES para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (measurement.tubos && measurement.tubosOpciones.length === 0) {
        alert(
          `Por favor, selecciona al menos una opción de TUBOS para ${measurement.nombreAbertura}`
        );
        return;
      }
      if (measurement.colocacion.length === 0) {
        alert(
          `Por favor, selecciona al menos un tipo de colocación para ${measurement.nombreAbertura}`
        );
        return;
      }
    }
    const doc = new jsPDF();

    const logo =
      "data:image/png;base64,";
    doc.addImage(logo, "PNG", 10, 10, 30, 16); // Parámetros: (imagenBase64, formato, x, y, ancho, alto)

    //Encabezado
    doc.setFont("helvetica", "bold");
    doc.setFontSize(20);
    doc.text("Aberturas Ferrero", 45, 15);

    doc.setFontSize(16);
    doc.setFont("helvetica", "normal");

    doc.text("Trabajo a Presupuestar", 45, 25);

    //Fecha
    doc.setFontSize(12);
    doc.text(
      `${new Date().getDate().toString().padStart(2, "0")}/${(
        new Date().getMonth() + 1
      )
        .toString()
        .padStart(2, "0")}/${new Date().getFullYear()}`,
      180,
      12
    );

    // Información del cliente
    doc.setFontSize(12);
    doc.text(`${clientInfo.nombre}`, 150, 20);
    doc.text(`${clientInfo.celular}`, 150, 25);
    doc.text(`${clientInfo.direccion}`, 150, 30);

    measurements.forEach((measurement, index) => {
      const baseY = 40 + index * 58;
      doc.setFontSize(14);
      doc.setFont("Courier", "bold");
      doc.text(`${measurement.nombreAbertura}`, 10, baseY);
      doc.setFont("helvetica", "normal");

      doc.setFontSize(12);
      const cantidadGuiasHojasText =
        measurement.cantidadGuiasHojas.length > 0
          ? `${measurement.cantidadGuiasHojas.join(", ")}`
          : "";

      doc.text(
        `Cant: ${measurement.cantidad} ••• ${measurement.ancho} x ${
          measurement.length
        } cm ••• ${measurement.tipoAbertura} ${
          measurement.modoApertura === "Corrediza"
            ? `Corrediza (${cantidadGuiasHojasText})`
            : `Apertura: ${measurement.modoApertura}`
        } ••• Vidrio: ${measurement.tipoVidrio.join(", ")}`,
        10,
        baseY + 8
      );

      doc.text(
        `Serie: ${measurement.series.join(
          ", "
        )} ••• Color: ${measurement.color.join(", ")}`,
        10,
        baseY + 13
      );

      doc.text(
        `Mosquitero: ${measurement.mosquitero ? "" : "No"}${
          measurement.mosquitero
            ? `${measurement.mosquiteroOpciones.join(", ")}`
            : ""
        }`,
        10,
        baseY + 18
      );

      doc.text(
        `Monobloc: ${measurement.monobloc ? "Sí" : "No"}${
          measurement.monobloc
            ? ` Cajón: ${measurement.monoblocCajonOpciones.join(
                ", "
              )} ••• Lamas: ${measurement.monoblocLamasOpciones.join(
                ", "
              )} ••• Tamaño: ${measurement.monoblocLamasTamOpciones.join(", ")}`
            : ""
        }`,
        10,
        baseY + 23
      );
      doc.text(
        `${
          measurement.cierres
            ? `Cierres: ${measurement.cierres.join(", ")}`
            : "No"
        } ••• Cerraduras: ${
          measurement.cerraduras === 0 ? "No" : measurement.cerraduras
        }`,
        10,
        baseY + 28
      );

      doc.text(
        `Tapajuntas: ${measurement.tapajuntas ? "Sí" : "No"} ••• Premarco: ${
          measurement.premarco ? "Sí" : "No"
        } ••• Colocación: ${measurement.colocacion.join(", ")}`,
        10,
        baseY + 33
      );

      doc.text(
        `Conectores: ${
          measurement.conectores
            ? measurement.conectoresOpciones.join(", ")
            : "No"
        }`,
        10,
        baseY + 38
      );

      doc.text(
        `Tubos: ${
          measurement.tubos ? measurement.tubosOpciones.join(", ") : "No"
        }`,
        10,
        baseY + 43
      );

      // Detalles adicionales
      doc.text(
        `Detalles: ${
          measurement.detalles.length ? measurement.detalles : "No"
        }`,
        10,
        baseY + 48
      );
    });

    doc.save(
      `Presupuesto-${clientInfo.nombre}-${new Date().toLocaleDateString(
        "es-ES"
      )}.pdf`
    );
  };

  useEffect(() => {
    // console.log(measurements[0].conectores);
    // console.log(measurements[0].conectoresOpciones);
    // console.log(measurements[0].tubos);
    // console.log("Cajon: " + measurements[0].monoblocCajonOpciones.length);
    // console.log("Lamas: " + measurements[0].monoblocLamasOpciones.length);
    // console.log("Tamano: " + measurements[0].monoblocLamasTamOpciones.length);
  }, [measurements]);
  return (
    <div className="measurement-container">
      <h2>Presupuestar Aberturas</h2>
      <form id="formId" onSubmit={handleSubmit}>
        <h3>Datos del Cliente</h3>
        <div className="customerInfo-container">
          <label className="label-text">
            <h4>Nombre:</h4>
            <input
              type="text"
              name="nombre"
              value={clientInfo.nombre}
              onChange={handleClientInputChange}
              required
            />
          </label>
          <label className="label-text">
            <h4>Celular:</h4>
            <input
              type="text"
              name="celular"
              value={clientInfo.celular}
              onChange={handleClientInputChange}
            />
          </label>
          <label className="label-text">
            <h4> Dirección:</h4>
            <input
              type="text"
              name="direccion"
              value={clientInfo.direccion}
              onChange={handleClientInputChange}
            />
          </label>
          <label className="label-text">
            <h4>Email:</h4>
            <input
              type="email"
              name="email"
              value={clientInfo.email}
              onChange={handleClientInputChange}
            />
          </label>
        </div>
        <Card.Body>
          <Accordion defaultActiveKey="0">
            {measurements.map((measurement, index) => (
              <Accordion.Item eventKey={index.toString()} key={index}>
                <div className="accordion-header-container">
                  <Accordion.Header>
                    <div className="horizontal">
                      {measurement.nombreAbertura}
                    </div>
                  </Accordion.Header>
                  <button
                    className="remove-button"
                    type="button"
                    onClick={() => handleShowModal(index)} // Pasa el index al modal
                  >
                    <FontAwesomeIcon
                      icon={faTrash}
                      style={{ color: "#ffffff" }}
                    />
                  </button>
                </div>

                <Accordion.Body>
                  <div className="groupHorizontal-container">
                    <label className="label-text">
                      <h4> Nombre Abertura:</h4>
                      <input
                        type="text"
                        name="nombreAbertura"
                        value={measurement.nombreAbertura}
                        onChange={(e) => handleInputChange(index, e)}
                        required
                      />
                    </label>
                    <label className="label-text">
                      <h4> Cantidad:</h4>
                      <input
                        type="number"
                        name="cantidad"
                        value={measurement.cantidad}
                        onChange={(e) => handleInputChange(index, e)}
                        min={0}
                        inputMode="numeric"
                        required
                      />
                    </label>
                  </div>
                  <div className="groupHorizontal-container border-box">
                    <label className="label-text">
                      <h4> Ancho (cm):</h4>
                      <input
                        type="text"
                        name="ancho"
                        value={measurement.ancho}
                        onChange={(e) => handleInputChange(index, e)}
                        inputMode="decimal"
                        pattern="^\d+(\.\d+)?$"
                        step="any"
                        min={0}
                        required
                      />
                    </label>
                    <label className="label-text">
                      <h4> Alto (cm):</h4>
                      <input
                        type="text"
                        name="length"
                        value={measurement.length}
                        onChange={(e) => handleInputChange(index, e)}
                        inputMode="decimal"
                        pattern="^\d+(\.\d+)?$"
                        step="any"
                        min={0}
                        required
                      />
                    </label>
                  </div>
                  <div className="groupHorizontal-container">
                    <label className="label-text">
                      <h4>Tipo de Abertura:</h4>
                      <select
                        name="tipoAbertura"
                        value={measurement.tipoAbertura}
                        onChange={(e) => handleInputChange(index, e)}
                        required
                      >
                        <option value="">Selecciona</option>
                        {tipoAberturaOptions.map((tipo) => (
                          <option key={tipo} value={tipo}>
                            {tipo}
                          </option>
                        ))}
                      </select>
                    </label>
                    <label className="label-text">
                      <h4>Modo de Apertura:</h4>
                      <select
                        name="modoApertura"
                        value={measurement.modoApertura}
                        onChange={(e) => handleInputChange(index, e)}
                        required
                        disabled={
                          measurement.tipoAbertura === "Otro" ||
                          measurement.tipoAbertura === ""
                        }
                      >
                        <option value="">Selecciona</option>
                        {modoAperturaOptions
                          .filter((modo) => {
                            if (measurement.tipoAbertura === "Ventana") {
                              return modo !== "Adentro" && modo !== "Afuera";
                            }

                            if (measurement.tipoAbertura === "Puerta") {
                              return modo === "Adentro" || modo === "Afuera";
                            }
                            return true;
                          })
                          .map((modo) => (
                            <option key={modo} value={modo}>
                              {modo}
                            </option>
                          ))}
                      </select>
                    </label>
                  </div>
                  <div className="groupHorizontal-container">
                    <label className="label-text ">
                      <h4> Serie:</h4>
                      {serieOptions.map((serie) => (
                        <label
                          key={serie}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="series"
                            value={serie}
                            checked={measurement.series.includes(serie)}
                            onChange={() =>
                              handleCheckboxChange(index, "series", serie)
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{serie}</span>
                        </label>
                      ))}
                    </label>

                    <label className="label-text">
                      <h4> Color:</h4>
                      {colorOptions.map((color) => (
                        <label
                          key={color}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="color"
                            value={color}
                            checked={measurement.color.includes(color)}
                            onChange={() =>
                              handleCheckboxChange(index, "color", color)
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{color}</span>
                        </label>
                      ))}
                    </label>
                  </div>

                  <div className="groupHorizontal-container border-box">
                    <label className="label-text">
                      <h4>Guías y Hojas:</h4>
                      {guiasHojasOptions.map((opcion) => (
                        <label
                          key={opcion}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="cantidadGuiasHojas"
                            value={opcion}
                            checked={
                              measurement.modoApertura === "Corrediza" &&
                              measurement.cantidadGuiasHojas.includes(opcion)
                            }
                            disabled={measurement.modoApertura !== "Corrediza"}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "cantidadGuiasHojas",
                                opcion
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{opcion}</span>
                        </label>
                      ))}
                    </label>

                    <label className="label-text">
                      <h4> Tipo de Vidrio:</h4>
                      {vidrioOptions.map((vidrio) => (
                        <label
                          key={vidrio}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="tipoVidrio"
                            value={vidrio}
                            checked={measurement.tipoVidrio.includes(vidrio)}
                            onChange={() =>
                              handleCheckboxChange(index, "tipoVidrio", vidrio)
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{vidrio}</span>
                        </label>
                      ))}
                    </label>
                  </div>
                  <div className="groupHorizontal-container border-box">
                    <label className="label-text horizontal cr-wrapper">
                      <h4> Mosquitero</h4>
                      <input
                        type="checkbox"
                        name="mosquitero"
                        checked={measurement.mosquitero}
                        onChange={(e) => handleInputChange(index, e)}
                      />
                      <div className="cr-input"></div>
                    </label>
                    <label className="label-text">
                      <h4>Tipo de mosquitero:</h4>
                      {mosquiteroOptions.map((mosquitero) => (
                        <label
                          key={mosquitero}
                          className="checkbox-label cr-wrapper"
                          required={measurement.mosquitero === true}
                        >
                          <input
                            type="checkbox"
                            name="mosquiteroOpciones"
                            value={mosquitero}
                            checked={measurement.mosquiteroOpciones.includes(
                              mosquitero
                            )}
                            disabled={measurement.mosquitero === false}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "mosquiteroOpciones",
                                mosquitero
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{mosquitero}</span>
                        </label>
                      ))}
                    </label>
                  </div>
                  <div className="groupHorizontal-container border-box">
                    <label className="label-text horizontal cr-wrapper">
                      <h4> Monobloc</h4>
                      <input
                        type="checkbox"
                        name="monobloc"
                        checked={measurement.monobloc}
                        onChange={(e) => handleInputChange(index, e)}
                      />
                      <div className="cr-input"></div>
                    </label>
                    <label className="label-text ">
                      <h4>Cajón Opciones</h4>
                      {monoblocCajonOptions.map((cajon) => (
                        <label
                          key={cajon}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="monoblocCajon"
                            value={cajon}
                            checked={measurement.monoblocCajonOpciones.includes(
                              cajon
                            )}
                            disabled={measurement.monobloc === false}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "monoblocCajonOpciones",
                                cajon
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{cajon}</span>
                        </label>
                      ))}
                    </label>
                    <label className="label-text">
                      <h4>Lamas</h4>
                      {monoblocLamasOptions.map((laminas) => (
                        <label
                          key={laminas}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="monoblocLamasOpciones"
                            value={laminas}
                            checked={measurement.monoblocLamasOpciones.includes(
                              laminas
                            )}
                            disabled={measurement.monobloc === false}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "monoblocLamasOpciones",
                                laminas
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{laminas}</span>
                        </label>
                      ))}
                    </label>
                    <label className="label-text">
                      <h4>Tamaño Lamas</h4>
                      {monoblocLamasTamOptions.map((tam) => (
                        <label key={tam} className="checkbox-label cr-wrapper">
                          <input
                            type="checkbox"
                            name="monoblocLamasTamOpciones"
                            value={tam}
                            checked={measurement.monoblocLamasTamOpciones.includes(
                              tam
                            )}
                            disabled={measurement.monobloc === false}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "monoblocLamasTamOpciones",
                                tam
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{tam}</span>
                        </label>
                      ))}
                    </label>
                  </div>
                  <div className="groupHorizontal-container border-box">
                    <label className="label-text">
                      <h4>Cierres:</h4>
                      {cierresOptions.map((cierre) => (
                        <label
                          key={cierre}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="cierres"
                            value={cierre}
                            checked={measurement.cierres.includes(cierre)}
                            disabled={measurement.tipoAbertura === "Puerta"}
                            onChange={() =>
                              handleCheckboxChange(index, "cierres", cierre)
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{cierre}</span>
                        </label>
                      ))}
                    </label>
                    <label className="label-text">
                      <h4> Cantidad cerraduras:</h4>
                      <input
                        type="number"
                        name="cerraduras"
                        value={measurement.cerraduras}
                        onChange={(e) => handleInputChange(index, e)}
                        min={0}
                        inputMode="numeric"
                      />
                    </label>
                  </div>
                  <div className="groupHorizontal-container border-box">
                    <label className="label-text horizontal cr-wrapper">
                      <h4>Tapajuntas</h4>
                      <input
                        type="checkbox"
                        name="tapajuntas"
                        checked={measurement.tapajuntas}
                        onChange={(e) => handleInputChange(index, e)}
                      />
                      <div className="cr-input"></div>
                    </label>

                    <label className="label-text horizontal cr-wrapper">
                      <h4>Premarco</h4>
                      <input
                        type="checkbox"
                        name="premarco"
                        checked={measurement.premarco}
                        onChange={(e) => handleInputChange(index, e)}
                      />
                      <div className="cr-input"></div>
                    </label>
                  </div>
                  <div className="groupHorizontal-container border-box">
                    <label className="label-text horizontal cr-wrapper">
                      <h4>Conectores</h4>
                      <input
                        type="checkbox"
                        name="conectores"
                        checked={measurement.conectores}
                        onChange={(e) => handleInputChange(index, e)}
                      />
                      <div className="cr-input"></div>
                    </label>
                    <label className="label-text ">
                      <h4>Opciones de Conectores:</h4>
                      {conectoresTubosOptions.map((opcion) => (
                        <label
                          key={opcion}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="conectoresOpciones"
                            checked={measurement.conectoresOpciones.includes(
                              opcion
                            )}
                            disabled={!measurement.conectores}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "conectoresOpciones",
                                opcion
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{opcion}</span>
                        </label>
                      ))}
                    </label>
                  </div>

                  <div className="groupHorizontal-container border-box">
                    <label className="label-text horizontal cr-wrapper">
                      <h4> Tubos</h4>
                      <input
                        type="checkbox"
                        name="tubos"
                        checked={measurement.tubos}
                        onChange={(e) => handleInputChange(index, e)}
                      />
                      <div className="cr-input"></div>
                    </label>
                    <label className="label-text ">
                      <h4> Opciones de Tubos:</h4>
                      {conectoresTubosOptions.map((opcion) => (
                        <label
                          key={opcion}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="tubosOpciones"
                            checked={measurement.tubosOpciones.includes(opcion)}
                            disabled={!measurement.tubos}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "tubosOpciones",
                                opcion
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{opcion}</span>
                        </label>
                      ))}
                    </label>
                  </div>
                  <div className="groupHorizontal-container">
                    <label className="label-text">
                      <h4> Colocación:</h4>
                      {colocacionOptions.map((colocacion) => (
                        <label
                          key={colocacion}
                          className="checkbox-label cr-wrapper"
                        >
                          <input
                            type="checkbox"
                            name="colocacion"
                            value={colocacion}
                            checked={measurement.colocacion.includes(
                              colocacion
                            )}
                            onChange={() =>
                              handleCheckboxChange(
                                index,
                                "colocacion",
                                colocacion
                              )
                            }
                          />
                          <div className="cr-input"></div>
                          <span>{colocacion}</span>
                        </label>
                      ))}
                    </label>
                  </div>
                  <div className="groupHorizontal-container">
                    <label className="label-text ">
                      <h4> Detalles:</h4>
                      <textarea
                        name="detalles"
                        value={measurement.detalles}
                        onChange={(e) => handleInputChange(index, e)}
                        rows="4"
                        cols="50"
                      />
                    </label>
                  </div>
                </Accordion.Body>
              </Accordion.Item>
            ))}
          </Accordion>
        </Card.Body>
      </form>
      <div className="btn-container">
        <button
          className="btn btn-warning"
          type="button"
          onClick={handleAddMeasurement}
        >
          Agregar Abertura
        </button>
        <button
          className="btn btn-danger"
          type="button"
          onClick={() => {
            const form = document.getElementById("formId");
            if (form) {
              form.requestSubmit();
            }
          }}
        >
          Generar PDF
        </button>
      </div>
      {showModal && (
        <GenericModal
          onConfirm={handleConfirmDelete}
          onClose={() => setShowModal(false)}
          itemName={measurements[currentIndex]?.nombreAbertura}
          buttonText="Eliminar"
        />
      )}
    </div>
  );
};

export default Measurements;
